                <FormStack>
                    <FormRow>
                        <LabelInput text={<>V<sub>in</sub></>} 
                            type="number"
                            onChange={handleVin} value={inputState.at(0)} />
                        <ResistorSelector text={<>R<sub>1</sub></>} 
                            onSelect={handleR2} 
                            value={inputState.at(2)} />
                    </FormRow>
                    <Button onClick={handleSwitch}>switch</Button>
                    <ResistorSelector text={<>R<sub>f</sub></>} 
                        onSelect={handleR1} 
                        value={inputState.at(1)} />
                    <FormRow>
                        <LabelOutput text={<>V<sub>out</sub></>} value={outputState} readOnly />
                        <LabelOutput text={<>A<sub>(v)</sub></>} value={gainState} readOnly />
                    </FormRow>
                </FormStack>
